CREATE TABLE IF NOT EXISTS "curriculum_experience" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title_pt_br" varchar(250) NOT NULL, "title_en_us" varchar(250) NOT NULL, "organization" varchar(250) NOT NULL, "place" varchar(250) NOT NULL, "description_pt_br" text NOT NULL, "description_en_us" text NOT NULL, "key_achievement_pt_br" varchar(250) NOT NULL, "key_achievement_en_us" varchar(250) NOT NULL, "since" date NOT NULL, "until" date NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "relevance_id" integer NULL REFERENCES "curriculum_relevance" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "curriculum_skill" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "description_pt_br" text NOT NULL, "description_en_us" text NOT NULL, "level" integer NOT NULL, "name_pt_br" varchar(100) NOT NULL, "name_en_us" varchar(100) NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "icon" varchar(100) NOT NULL, "category_id" integer NULL REFERENCES "curriculum_category" ("id") DEFERRABLE INITIALLY DEFERRED, "relevance_id" integer NULL REFERENCES "curriculum_relevance" ("id") DEFERRABLE INITIALLY DEFERRED, "parent_id" integer NULL REFERENCES "curriculum_skill" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "curriculum_relevance" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "value" varchar(4) NOT NULL, "description_pt_br" text NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "order" integer NOT NULL);
CREATE TABLE IF NOT EXISTS "curriculum_course" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "description_pt_br" text NOT NULL, "description_en_us" text NOT NULL, "since" date NOT NULL, "until" date NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "place_en_us" varchar(250) NOT NULL, "relevance_id" integer NULL REFERENCES "curriculum_relevance" ("id") DEFERRABLE INITIALLY DEFERRED, "name_pt_br" varchar(250) NOT NULL, "name_en_us" varchar(250) NOT NULL, "place_pt_br" varchar(250) NOT NULL);
CREATE TABLE IF NOT EXISTS "curriculum_certificate" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title_pt_br" varchar(100) NOT NULL, "title_en_us" varchar(100) NOT NULL, "date" date NULL, "file" varchar(100) NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "course_id" integer NULL REFERENCES "curriculum_course" ("id") DEFERRABLE INITIALLY DEFERRED, "relevance_id" integer NULL REFERENCES "curriculum_relevance" ("id") DEFERRABLE INITIALLY DEFERRED);
